do_sphere（R、FREQ、X、Y、Z）

  ダブルR;
  INT FREQ。
  ダブルX;
  ダブルY;
  ダブルZ;

  {

    int型のポール。
    ダブルnorthy、southy、poley。
    ダブルrtheta、rtheta2、ntheta個、ntheta2、magicangle。
    ダブルシータ、thetastart、thisy、デン、T。
    構造体Point_node * PNP。
    構造体Point_struct P1、P2、P3、P4、N1、N2、N3、N4、PT。

    半径= R。
    Xorg = X。
    yorg = Y。
    ZORG = Z。

/* 北極 */

    magicangle = 30.0 * PI / 180.0;
    northy =半径* SIN（magicangle）。
    southy = -radius *罪（magicangle）。
    用（ポール= 0;ポール<2;極++）{

      IF（極== 0）{

        poley =半径。
        thisy = northy。
        thetastart = 0.0。

      }
      そうしないと {

        poley = -radius。
        thisy = southy。
        thetastart = 36.0。

      }
      用（シータ= thetastart;シータ<360.0;シータ+ = 60.0）{

        rtheta =シータ* PI / 180.0;
        rtheta2 =（シータ+ 60.0）* PI / 180.0;
        p1.x = 0.0。
        p1.y = poley。
        p1.z = 0.0。
        p2.x =半径* COS（rtheta）。
        p2.y = thisy。
        p2.z =半径* SIN（rtheta）。
        p3.x =半径* COS（rtheta2）。
        p3.y = thisy。
        p3.z =半径* SIN（rtheta2）。

        IF（極== 0）{

/ *メイクリングは、法線が右*ですので、他の道を行きます/

          pt.x = p3.x。
          pt.y = p3.y。
          pt.z = p3.z。
          p3.x = p2.x。
          p3.y = p2.y。
          p3.z = p2.z。
          p2.x = pt.x。
          p2.y = pt.y。
          p2.z = pt.z。

        }

        DEN =（p1.x * p1.x）+（p1.y * p1.y）+（p1.z * p1.z）。
        DEN = SQRT（DEN）。

        （デン！= 0.0）の場合{

          T =半径/ DEN。
          p1.x * = T;
          p1.y * = T;
          p1.z * = T;

        }

        DEN =（p2.x * p2.x）+（p2.y * p2.y）+（p2.z * p2.z）。
        DEN = SQRT（DEN）。

        （デン！= 0.0）の場合{

          T =半径/ DEN。
          p2.x * = T;
          p2.y * = T;
          p2.z * = T;

        }

        DEN =（p3.x * p3.x）+（p3.y * p3.y）+（p3.z * p3.z）。
        DEN = SQRT（DEN）。

        （デン！= 0.0）の場合{

          T =半径/ DEN。
          p3.x * = T;
          p3.y * = T;
          p3.z * = T;

        }

        subdivide_tri（＆P1＆P2、およびP3、FREQ）。

      }

    }

/ *今の体* /

    用（シータ= 0.0;シータ<360.0;シータ+ = 60.0）{

      rtheta =シータ* PI / 180.0;
      rtheta2 =（シータ+ 60.0）* PI / 180.0;
      ntheta個=（シータ+ 36.0）* PI / 180.0。
      ntheta2 =（シータ+ 96.0）* PI / 180.0;
      p1.x =半径* COS（rtheta）。
      p1.y = northy。
      p1.z =半径* SIN（rtheta）。
      p2.x =半径* COS（rtheta2）。
      p2.y = northy。
      p2.z =半径* SIN（rtheta2）。
      p3.x =半径* COS（ntheta個）。
      p3.y = southy。
      p3.z =半径* SIN（ntheta個）。
      p4.x =半径* COS（ntheta2）。
      p4.y = southy。
      p4.z =半径* SIN（ntheta2）。

      DEN =（p1.x * p1.x）+（p1.y * p1.y）+（p1.z * p1.z）。
      DEN = SQRT（DEN）。

      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p1.x * = T;
        p1.y * = T;
        p1.z * = T;

      }

      DEN =（p2.x * p2.x）+（p2.y * p2.y）+（p2.z * p2.z）。
      DEN = SQRT（DEN）。

      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p2.x * = T;
        p2.y * = T;
        p2.z * = T;

      }
      DEN =（p3.x * p3.x）+（p3.y * p3.y）+（p3.z * p3.z）。
      DEN = SQRT（DEN）。
      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p3.x * = T;
        p3.y * = T;
        p3.z * = T;

      }
      DEN =（p4.x * p4.x）+（p4.y * p4.y）+（p4.z * p4.z）。
      DEN = SQRT（DEN）。
      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p4.x * = T;
        p4.y * = T;
        p4.z * = T;

      }

      subdivide_tri（＆P1＆P2、およびP3、FREQ）。
      subdivide_tri（＆P3＆P2、およびP4、FREQ）。

    }

    リターン;

  }

#define norm_pt（V）{二重Rレジスタ= SQRT（（（V） - > X *（V） - > X）+ \
                                               （（V） - > Y *（V） - > Y）+ \
                                               （（V） - > Z *（V） - > Z））。 \
                     （V） - > X / R =。 \
                     （V） - > Y / = R。 \
                     （V） - > Z / = R。 \
                    }

subdivide_tri（P1、P2、P3、A）

  構造体Point_struct * P1、P2 *、* P3。
  int型;

  {

    構造体Point_struct N1、N2、N3;
    構造体Point_structのP12、P13、P23;
    ダブルデン、T。

    IF（> 0）{

      p12.x =（P1-> X + P2-> X）/2.0。
      p12.y =（P1-> Y + P2-> Y）/2.0。
      p12.z =（P1-> Z + P2-> Z）/2.0。
      DEN =（p12.x * p12.x）+（p12.y * p12.y）+（p12.z * p12.z）。
      DEN = SQRT（DEN）。
      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p12.x * = T;
        p12.y * = T;
        p12.z * = T;

      }
      p13.x =（P1-> X + P3-> X）/2.0。
      p13.y =（P1-> Y + P3-> Y）/2.0。
      p13.z =（P1-> Z + P3-> Z）/2.0。
      DEN =（p13.x * p13.x）+（p13.y * p13.y）+（p13.z * p13.z）。
      DEN = SQRT（DEN）。
      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p13.x * = T;
        p13.y * = T;
        p13.z * = T;

      }
      p23.x =（P2-> X + P3-> X）/2.0。
      p23.y =（P2-> Y + P3-> Y）/2.0。
      p23.z =（P2-> Z + P3-> Z）/2.0。
      DEN =（p23.x * p23.x）+（p23.y * p23.y）+（p23.z * p23.z）。
      DEN = SQRT（DEN）。
      （デン！= 0.0）の場合{

        T =半径/ DEN。
        p23.x * = T;
        p23.y * = T;
        p23.z * = T;

      }
      subdivide_tri（P1、およびP12、およびP13、-1）;
      subdivide_tri（＆P12、P2、およびP23、-1）;
      subdivide_tri（＆P13、およびP23、P3、-1）;
      subdivide_tri（＆P12、およびP23、およびP13、-1）;

    } そうしないと {

      n1.x = P1-> X。
      n1.y = P1-> Y。
      n1.z = P1-> Z。
      norm_pt（＆N1）。
      n2.x = P2-> X。
      n2.y = P2-> Y。
      n2.z = P2-> Z。
      norm_pt（＆N2）。
      n3.x = P3-> X。
      n3.y = P3-> Y。
      n3.z = P3-> Z。
      norm_pt（＆N3）。

/ *このポリについて何も特別な* /

      printf（ "％F％F％F％F％F％F \ n" は、P1-> X +のXorg、
                                      P1-> Y + yorg、
                                      P1-> Z + ZORG、
                                      n1.x、n1.y、n1.z）。
      printf（ "％F％F％F％F％F％F \ n" は、P2-> X +のXorg、
                                      P2-> Y + yorg、
                                      P2-> Z + ZORG、
                                      n2.x、n2.y、n2.z）。
      printf（ "％F％F％F％F％F％F \ n" は、P3-> X +のXorg、
                                      P3-> Y + yorg、
                                      P3-> Z + ZORG、
                                      n3.x、n3.y、n3.z）。

    }

    リターン;

  }
